import{_ as d,c as e,o as t,V as o}from"./chunks/framework.CsbsVE7l.js";const b=JSON.parse('{"title":"Git Flow 规范","description":"Git Flow 工作流程规范","frontmatter":{"title":"Git Flow 规范","description":"Git Flow 工作流程规范"},"headers":[],"relativePath":"frontend/engineering/guide/branch-management.md","filePath":"frontend/engineering/guide/branch-management.md","lastUpdated":1749784010000}'),l={name:"frontend/engineering/guide/branch-management.md"},a=o('<h1 id="git-flow-规范" tabindex="-1">Git Flow 规范 <a class="header-anchor" href="#git-flow-规范" aria-label="Permalink to &quot;Git Flow 规范&quot;">​</a></h1><h2 id="分支管理" tabindex="-1">分支管理 <a class="header-anchor" href="#分支管理" aria-label="Permalink to &quot;分支管理&quot;">​</a></h2><h3 id="分支类型说明" tabindex="-1">分支类型说明： <a class="header-anchor" href="#分支类型说明" aria-label="Permalink to &quot;分支类型说明：&quot;">​</a></h3><ol><li><strong>主分支</strong>：长期存在的核心分支</li><li><strong>功能分支</strong>：用于新功能开发</li><li><strong>修复分支</strong>：用于问题修复</li><li><strong>重构分支</strong>：用于代码重构</li><li><strong>优化分支</strong>：用于性能优化</li><li><strong>紧急修复分支</strong>：用于生产环境紧急修复</li><li><strong>发布分支</strong>：用于版本发布准备</li><li><strong>其他分支</strong>：包括格式、文档、构建、CI、测试、工具配置等</li></ol><h2 id="分支生命周期" tabindex="-1">分支生命周期 <a class="header-anchor" href="#分支生命周期" aria-label="Permalink to &quot;分支生命周期&quot;">​</a></h2><h3 id="主分支生命周期" tabindex="-1">主分支生命周期 <a class="header-anchor" href="#主分支生命周期" aria-label="Permalink to &quot;主分支生命周期&quot;">​</a></h3><ul><li><code>main</code>：永久存在，只接受 <code>release</code> 和 <code>hotfix</code> 分支的合并</li><li><code>develop</code>：永久存在，作为开发的主干分支</li><li><code>test</code>：永久存在，用于测试环境部署</li><li><code>uat</code>：永久存在，用于用户验收测试</li></ul><h3 id="功能分支生命周期" tabindex="-1">功能分支生命周期 <a class="header-anchor" href="#功能分支生命周期" aria-label="Permalink to &quot;功能分支生命周期&quot;">​</a></h3><ul><li>创建：从 <code>develop</code> 分支创建</li><li>存活期：功能开发完成并合并后删除</li><li>最长存活期：建议不超过 2 周</li><li>合并后：必须删除</li></ul><h3 id="修复分支生命周期" tabindex="-1">修复分支生命周期 <a class="header-anchor" href="#修复分支生命周期" aria-label="Permalink to &quot;修复分支生命周期&quot;">​</a></h3><ul><li><code>hotfix</code>：创建后 24 小时内必须完成修复并合并</li><li><code>bugfix</code>：创建后 3 天内必须完成修复并合并</li><li>合并后：必须删除</li></ul><h3 id="发布分支生命周期" tabindex="-1">发布分支生命周期 <a class="header-anchor" href="#发布分支生命周期" aria-label="Permalink to &quot;发布分支生命周期&quot;">​</a></h3><ul><li>创建：从 <code>develop</code> 分支创建</li><li>存活期：版本发布完成后删除</li><li>最长存活期：建议不超过 1 周</li><li>合并后：必须删除</li></ul><h2 id="工作流程规范" tabindex="-1">工作流程规范 <a class="header-anchor" href="#工作流程规范" aria-label="Permalink to &quot;工作流程规范&quot;">​</a></h2><h3 id="分支创建流程" tabindex="-1">分支创建流程 <a class="header-anchor" href="#分支创建流程" aria-label="Permalink to &quot;分支创建流程&quot;">​</a></h3><ol><li>创建前：确保本地代码与目标分支同步</li><li>创建时：遵循命名规范</li><li>创建后：及时推送到远程仓库</li></ol><h3 id="开发工作流程" tabindex="-1">开发工作流程 <a class="header-anchor" href="#开发工作流程" aria-label="Permalink to &quot;开发工作流程&quot;">​</a></h3><ol><li>每日工作开始：拉取最新代码</li><li>开发过程中：定期提交代码（建议每天至少一次）</li><li>提交信息：遵循约定式提交规范</li><li>代码审查：必须通过 Pull Request</li></ol><h3 id="合并流程" tabindex="-1">合并流程 <a class="header-anchor" href="#合并流程" aria-label="Permalink to &quot;合并流程&quot;">​</a></h3><ol><li>合并前： <ul><li>确保代码通过所有测试</li><li>确保代码通过代码审查</li><li>解决所有冲突</li></ul></li><li>合并时： <ul><li>使用 Pull Request</li><li>填写完整的合并说明</li><li>指定至少一名审查者</li></ul></li><li>合并后： <ul><li>删除已合并的分支</li><li>更新相关文档</li></ul></li></ol><h3 id="发布流程" tabindex="-1">发布流程 <a class="header-anchor" href="#发布流程" aria-label="Permalink to &quot;发布流程&quot;">​</a></h3><ol><li>版本发布前： <ul><li>创建 <code>release</code> 分支</li><li>进行完整测试</li><li>更新版本号</li></ul></li><li>发布时： <ul><li>合并到 <code>main</code> 和 <code>develop</code></li><li>打版本标签</li></ul></li><li>发布后： <ul><li>删除 <code>release</code> 分支</li><li>更新发布文档</li></ul></li></ol><h3 id="紧急修复流程" tabindex="-1">紧急修复流程 <a class="header-anchor" href="#紧急修复流程" aria-label="Permalink to &quot;紧急修复流程&quot;">​</a></h3><ol><li>发现问题：立即创建 <code>hotfix</code> 分支</li><li>修复过程： <ul><li>优先修复问题</li><li>编写测试用例</li><li>进行代码审查</li></ul></li><li>修复完成后： <ul><li>合并到 <code>main</code> 和 <code>develop</code></li><li>打补丁版本标签</li><li>删除 <code>hotfix</code> 分支</li></ul></li></ol><h3 id="主要分支" tabindex="-1">主要分支 <a class="header-anchor" href="#主要分支" aria-label="Permalink to &quot;主要分支&quot;">​</a></h3><ul><li><code>main</code>: 主分支，用于生产环境</li><li><code>develop</code>: 开发分支，用于开发环境</li></ul><h3 id="辅助分支-是以关键词开口" tabindex="-1">辅助分支，是以关键词开口 <a class="header-anchor" href="#辅助分支-是以关键词开口" aria-label="Permalink to &quot;辅助分支，是以关键词开口&quot;">​</a></h3><ul><li><code>feat-*</code>: 功能分支</li><li><code>release-*</code>: 发布分支</li><li><code>hotfix-*</code>: 紧急修复分支</li><li><code>bugfix-*</code>: 问题修复分支</li></ul><h2 id="工作流程" tabindex="-1">工作流程 <a class="header-anchor" href="#工作流程" aria-label="Permalink to &quot;工作流程&quot;">​</a></h2><h3 id="功能开发" tabindex="-1">功能开发 <a class="header-anchor" href="#功能开发" aria-label="Permalink to &quot;功能开发&quot;">​</a></h3><ol><li>从 <code>develop</code> 创建 <code>feature</code> 分支</li><li>在 <code>feat</code> 分支开发, 有的团队使用<code>feature</code>,此处只是个人习惯</li><li>完成后合并回 <code>develop</code></li></ol><h3 id="版本发布" tabindex="-1">版本发布 <a class="header-anchor" href="#版本发布" aria-label="Permalink to &quot;版本发布&quot;">​</a></h3><ol><li>从 <code>develop</code> 创建 <code>release</code> 分支</li><li>在 <code>release</code> 分支进行测试和修复</li><li>完成后合并到 <code>main</code> 和 <code>develop</code></li></ol><h3 id="紧急修复" tabindex="-1">紧急修复 <a class="header-anchor" href="#紧急修复" aria-label="Permalink to &quot;紧急修复&quot;">​</a></h3><ol><li>从 <code>main</code> 创建 <code>hotfix</code> 分支</li><li>修复完成后合并到 <code>main</code> 和 <code>develop</code></li></ol><h2 id="分支命名规范" tabindex="-1">分支命名规范 <a class="header-anchor" href="#分支命名规范" aria-label="Permalink to &quot;分支命名规范&quot;">​</a></h2><h3 id="功能分支" tabindex="-1">功能分支 <a class="header-anchor" href="#功能分支" aria-label="Permalink to &quot;功能分支&quot;">​</a></h3><ul><li>格式：<code>feat-功能名称（小驼峰命名法）</code></li><li>示例：<code>feat-useLogin</code></li></ul><h4 id="分支功能命名规范个人推荐-便于团队协作" tabindex="-1">分支功能命名规范个人推荐，便于团队协作 <a class="header-anchor" href="#分支功能命名规范个人推荐-便于团队协作" aria-label="Permalink to &quot;分支功能命名规范个人推荐，便于团队协作&quot;">​</a></h4><ul><li>格式：<code>feat-功能名称（小驼峰命名法）</code>-开发者名称-分支创建的日期</li><li>示例：<code>feat-useLogin-zhangsan-20250613</code></li></ul><h3 id="发布分支" tabindex="-1">发布分支 <a class="header-anchor" href="#发布分支" aria-label="Permalink to &quot;发布分支&quot;">​</a></h3><ul><li>格式：<code>release/版本号</code>， 版本号取自当前项目的<code>package.json</code>文件中version</li><li>示例：<code>release/v1.0.0</code></li></ul><h3 id="修复分支" tabindex="-1">修复分支 <a class="header-anchor" href="#修复分支" aria-label="Permalink to &quot;修复分支&quot;">​</a></h3><ul><li>格式：<code>hotfix-问题描述</code></li><li>示例：<code>hotfix/login-error</code></li></ul><h2 id="合并规范" tabindex="-1">合并规范 <a class="header-anchor" href="#合并规范" aria-label="Permalink to &quot;合并规范&quot;">​</a></h2><ol><li>使用 Pull Request 进行代码审查</li><li>确保代码通过所有测试</li><li>保持提交信息清晰</li><li>删除已合并的分支</li></ol><h2 id="版本号的更新-有必要的话" tabindex="-1">版本号的更新（有必要的话） <a class="header-anchor" href="#版本号的更新-有必要的话" aria-label="Permalink to &quot;版本号的更新（有必要的话）&quot;">​</a></h2><ol><li><code>npm version patch</code> 升级小号</li><li><code>npm version minor</code> 升级中号</li><li><code>npm version major</code> 升级大号</li></ol><p>参考 <a href="/efficiency/skill/npmVersion.html">Npm 使用小技巧</a></p><h2 id="分支管理总表" tabindex="-1">分支管理总表 <a class="header-anchor" href="#分支管理总表" aria-label="Permalink to &quot;分支管理总表&quot;">​</a></h2><table><thead><tr><th>分支名称</th><th>分支类型</th><th>环境</th><th>用途说明</th><th>创建来源</th><th>合并目标</th><th>命名规范示例</th></tr></thead><tbody><tr><td><code>main</code></td><td>主分支</td><td>生产环境</td><td>生产环境部署，保持稳定</td><td>-</td><td>-</td><td>-</td></tr><tr><td><code>develop</code></td><td>主分支</td><td>开发环境</td><td>开发主分支，日常开发</td><td>-</td><td>-</td><td>-</td></tr><tr><td><code>test</code></td><td>主分支</td><td>测试环境</td><td>功能测试和集成测试</td><td>-</td><td>-</td><td>-</td></tr><tr><td><code>uat</code></td><td>主分支</td><td>用户验收环境</td><td>用户验收测试</td><td>-</td><td>-</td><td>-</td></tr><tr><td><code>feat-xxx</code></td><td>功能分支</td><td>开发环境</td><td>新功能开发</td><td><code>develop</code></td><td><code>develop</code></td><td><code>feat-userLogin-zhangsan-20240315</code></td></tr><tr><td><code>fix-xxx</code></td><td>修复分支</td><td>开发环境</td><td>Bug修复</td><td><code>develop</code></td><td><code>develop</code></td><td><code>fix-loginError-zhangsan-20240315</code></td></tr><tr><td><code>refactor-xxx</code></td><td>重构分支</td><td>开发环境</td><td>代码重构</td><td><code>develop</code></td><td><code>develop</code></td><td><code>refactor-authModule-zhangsan-20240315</code></td></tr><tr><td><code>perf-xxx</code></td><td>优化分支</td><td>开发环境</td><td>性能优化</td><td><code>develop</code></td><td><code>develop</code></td><td><code>perf-apiCache-zhangsan-20240315</code></td></tr><tr><td><code>hotfix-xxx</code></td><td>紧急修复分支</td><td>生产环境</td><td>生产环境紧急修复</td><td><code>main</code></td><td><code>main</code> 和 <code>develop</code></td><td><code>hotfix-paymentError-zhangsan-20240315</code></td></tr><tr><td><code>bugfix-xxx</code></td><td>问题修复分支</td><td>开发环境</td><td>问题修复</td><td><code>develop</code></td><td><code>develop</code></td><td><code>bugfix-uiDisplay-zhangsan-20240315</code></td></tr><tr><td><code>release-xxx</code></td><td>发布分支</td><td>预发布环境</td><td>版本发布准备</td><td><code>develop</code></td><td><code>main</code> 和 <code>develop</code></td><td><code>release/v1.0.0</code></td></tr><tr><td><code>style-xxx</code></td><td>格式分支</td><td>开发环境</td><td>代码格式修改</td><td><code>develop</code></td><td><code>develop</code></td><td><code>style-codeFormat-zhangsan-20240315</code></td></tr><tr><td><code>docs-xxx</code></td><td>文档分支</td><td>开发环境</td><td>文档更新</td><td><code>develop</code></td><td><code>develop</code></td><td><code>docs-apiUpdate-zhangsan-20240315</code></td></tr><tr><td><code>build-xxx</code></td><td>构建分支</td><td>开发环境</td><td>构建流程修改</td><td><code>develop</code></td><td><code>develop</code></td><td><code>build-webpackConfig-zhangsan-20240315</code></td></tr><tr><td><code>ci-xxx</code></td><td>CI分支</td><td>开发环境</td><td>CI配置修改</td><td><code>develop</code></td><td><code>develop</code></td><td><code>ci-githubActions-zhangsan-20240315</code></td></tr><tr><td><code>test-xxx</code></td><td>测试分支</td><td>开发环境</td><td>测试用例修改</td><td><code>develop</code></td><td><code>develop</code></td><td><code>test-loginFlow-zhangsan-20240315</code></td></tr><tr><td><code>chore-xxx</code></td><td>工具分支</td><td>开发环境</td><td>工具配置修改</td><td><code>develop</code></td><td><code>develop</code></td><td><code>chore-depsUpdate-zhangsan-20240315</code></td></tr><tr><td><code>revert-xxx</code></td><td>回滚分支</td><td>相关环境</td><td>代码回滚</td><td>相关分支</td><td>原分支</td><td><code>revert-feat123-zhangsan-20240315</code></td></tr></tbody></table>',51),i=[a];function c(r,n,h,s,u,x){return t(),e("div",null,i)}const p=d(l,[["render",c]]);export{b as __pageData,p as default};
